import { Component } from '@angular/core';

@Component({
  selector: 'app-doacao-recebida',
  templateUrl: './doacao-recebida.component.html',
  styleUrls: ['./doacao-recebida.component.css']
})
export class DoacaoRecebidaComponent {
    usuarioLogado = false;
   validarLogin(): boolean {

    if (this.usuario === 'admin' && this.senha === 1990){
      return true
    }
    return false;
   }
}

<body>
  <form action="">
    <div class="login">
      <h4>Acesso restrito, somente a usuários autorizados</h4>
      <input type="text" placeholder="Usuário" />
      <input type="password" placeholder="Senha" />
      <button class="button">Login</button>
    </div>
  </form>
</body>
