import { Component } from '@angular/core';

@Component({
  selector: 'app-doacao-recebida',
  templateUrl: './doacao-recebida.component.html',
  styleUrls: ['./doacao-recebida.component.css']
})
export class DoacaoRecebidaComponent {

}
<!DOCTYPE html>
<html lang="pt-br">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Formulário Centralizado</title>
  <style>
    /* Estilo para o body */

    /* Estilo para o formulário */
    .login {
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;
      background-color: white;
      padding: 20px;
      border-radius: 8px;
      box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
    }

    h4 {
      margin-bottom: 20px;
    }

    input {
      width: 200px;
      margin: 10px 0;
      padding: 10px;
      border: 1px solid #ccc;
      border-radius: 4px;
    }

    .button {
      width: 200px;
      padding: 10px;
      background-color: #4CAF50;
      color: white;
      border: none;
      border-radius: 4px;
      cursor: pointer;
      font-size: 16px;
    }

    .button:hover {
      background-color: #45a049;
    }
  </style>
</head>
<body>
  <form action="">
    <div class="login">
      <h4>Acesso restrito, somente a usuários autorizados</h4>
      <input type="text" placeholder="Usuário" />
      <input type="password" placeholder="Senha" />
      <button class="button">Login</button>
    </div>
  </form>
</body>
</html>
