import { Component } from '@angular/core';

@Component({
  selector: 'app-doacao-recebida',
  templateUrl: './doacao-recebida.component.html',
  styleUrls: ['./doacao-recebida.component.css']
})
export class DoacaoRecebidaComponent {
  usuarioLogado = false;
  usuario: string = '';
  senha: string = '';

   validarLogin(): boolean {
    if (this.usuario === 'admin' && this.senha === '1990'){
      this.usuarioLogado = true
    }
    return this.usuarioLogado;
   }
}
<h4>Lista de doações recebidas</h4>

<table class="table">
    <thead>
      <tr>
        <th>First</th>
        <th scope="col">Last</th>
      </tr>
    </thead>
    <tbody>
      <tr>
        <th scope="row">1</th>
        <td>Mark</td>
        <td>Otto</td>

      </tr>
      <tr>
        <th scope="row">2</th>
        <td>Jacob</td>
        <td>Thornton</td>

      </tr>
      <tr>
        <th scope="row">3</th>
        <td colspan="2">Larry the Bird</td>
      </tr>
    </tbody>
  </table>
