import { Component } from '@angular/core';
import { Doacao } from './interface/doacaoInterface';
import { DoacaoService } from './service/doacao.service';
import { Doador } from '../doador/interface/doadorInterface';

@Component({
  selector: 'app-doar',
  templateUrl: './doar.component.html',
  styleUrls: ['./doar.component.css'],
})
export class DoarComponent {

  doacao: Doacao = {
    id: 0,  // Inicialize com um valor padrão
    doador: {} as Doador,  // Inicialize com um objeto vazio ou um valor válido
    produto: [],  // Inicialize com um array vazio
    doacaoRecolhida: false,  // Inicialize com o valor padrão
    localDate: ''  // Inicialize com uma string vazia ou uma data padrão
  };

  constructor(private doacaoService: DoacaoService) { }

  salvarDoacao(): void {
    this.doacaoService.registrarDoacao(this.doacao).subscribe({
      next: (response) => {
        console.log('Doação registrada com sucesso!', response);
      },
      error: (error) => {
        console.error('Erro ao registrar a doação', error);
        // Aqui você pode tratar o erro, mostrando uma mensagem ao usuário
      },
      complete: () => {
        console.log('Requisição concluída.');
        // Aqui você pode executar algo quando a requisição for concluída
      }
    });
  }

}
